+                                                {{ subform.orcid(class="form-control", disabled=loop.first) }}
+                                                {% for error in subform.orcid.errors %}
+                                                    <span style="color: red;">{{ error }}</span>
+                                                {% endfor %}
+                                            </div>
+                                        </div>
+                                        {% endfor %}
+                                    </div>
