+                                        .then(response => {
+                                            if (!response.ok) {
+                                                // Manejo de errores HTTP
+                                                throw new Error(`Error ${response.status}: ${response.statusText}`);
+                                            }
+                                            return response.json();
+                                        })
+                                        .then(data => {
