+                                alert.textContent = 'Invalid file: ' + file.name;
+                                alerts.appendChild(alert);
+                                alerts.style.display = 'block';
+                            });
+                        }
+                    });
+                
+                    // Función para agregar el formulario de entrada a la lista de archivos
