+        color: #333;
+        display: inline-block;
+        margin-bottom: 10px;
+        position: relative;
+        padding-left: 20px;
+    }
+
+    .node h2 {
+        cursor: pointer;
+        font-weight: bold;
+        font-size: 16px;
+        color: #333;
+        display: inline-block;
+        margin-bottom: 10px;
+        position: relative;
+        padding-left: 20px;
+    }
+
+    .node label::before {
+        content: "►";
+        position: absolute;
+        left: 0;
+        top: 0;
+        font-size: 16px;
+        color: #3498db; /* Cambiado a azul */
