+                    <h2>{{ node.name }}</h2>
+                {% endif %}
+            {% endif %}
+            {% if node.name == "0" %}
+                <form method="POST" action="{{ url_for('botintegration.delete_node', node_id=node.id) }}" style="display: inline;">
+                    {{ form.hidden_tag() }}
+                    <input type="hidden" name="config_action" value="run">
+                    <button type="submit" class="run-stop-btn">Run</button>
