+                            ("7928339725:AAE6JYpPcd7x668IcIxIrlxwHB0Tx6DcqPI", "@uvlhub_bot2")
+                            ] %}
+                            <option value="{{ value }}">{{ label }}</option>
+                        {% endfor %}
+                </select>
+                <div class="col-4">
+                    {{ form.submit(class_="add-btn") }}
+                </div>
+            </div>
+        </form>
+    {% endif %}
+</div>
+{% endblock %}

