+    }
+
+    .node input[type="checkbox"]:checked ~ .children {
+        display: block;
+    }
+
+    .form-container {
+        display: flex;
+        flex-wrap: wrap;
+        align-items: center;
+        gap: 10px;
+    }
+
+    .form-container select,
+    .form-container input[type="text"],
+    .form-container .add-btn {
+        flex: 1;
+        min-width: 150px;
+        margin: 5px 0;
+        border-radius: 4px;
+        padding: 5px;
+        border: 1px solid #ccc;
+        font-size: 14px;
+    }
+
+    .add-btn {
+        background-color: #19cf47; /* Cambiado a azul */
+        border: none;
+        color: white;
+        cursor: pointer;
+        transition: background-color 0.3s;
+    }
+
+    .add-btn:hover {
+        background-color: #197a31; /* Azul más oscuro al pasar el ratón */
+    }
+
+    .delete-button {
+        background-color: #e74c3c; /* Sin cambios */
+        color: white;
+        border: none;
+        padding: 5px 10px;
+        border-radius: 4px;
+        cursor: pointer;
+        transition: background-color 0.3s;
+    }
+
+    .delete-button:hover {
+        background-color: #c0392b;
+    }
+
+    .node label:hover {
+        color: #3498db; /* Cambiado a azul */
