+
+    .run-stop-btn {
+        background-color: #3498db; /* Azul para el botón */
+        color: white;
+        border: none;
+        padding: 5px 15px;
+        border-radius: 4px;
+        cursor: pointer;
+        transition: background-color 0.3s;
