+
+    const featureModelConfig = {
+        type: 'bar',
+        data: featureModelData,
+        options: {
+            responsive: true,
+            plugins: {
+                legend: {
+                    display: false
+                },
+                tooltip: {
+                    callbacks: {
+                        label: function (context) {
+                            // Mostrar el porcentaje en el tooltip de "Total Downloads"
