+        let authors = document.getElementById(authorsId);
+        let id = authorsId.replace("_form_authors", "")
+        let newAuthor = createAuthorBlock(amount_authors, `feature_models-${id}-`);
+        authors.appendChild(newAuthor);
+
+    }
