+{% extends "base_template.html" %}
+
+{% block title %}Change Password{% endblock %}
+
+{% block content %}
+<h1 class="h3 mb-3">Change Password</h1>
+
+{% with messages = get_flashed_messages(with_categories=true) %}
+{% if messages %}
+    {% for category, message in messages %}
+    <div class="alert alert-{{ category }}" role="alert">
+        <div class="alert-message">{{ message }}</div>
+    </div>
+    {% endfor %}
+{% endif %}
+{% endwith %}
+
+<form method="POST" action="{{ url_for('profile.change_password') }}" class="needs-validation" novalidate>
+    {{ form.hidden_tag() }}
+
+    <div class="row mb-3">
+        <div class="col-12">
+            {{ form.password.label(class="form-label") }}
+            {{ form.password(class="form-control", placeholder="Enter new password") }}
+            {% for error in form.password.errors %}
+            <span style="color: red;">{{ error }}</span><br>
+            {% endfor %}
+        </div>
+    </div>
+
+    <div class="row mb-3">
+        <div class="col-12">
+            {{ form.confirm_password.label(class="form-label") }}
+            {{ form.confirm_password(class="form-control", placeholder="Confirm new password") }}
+            {% for error in form.confirm_password.errors %}
+            <span style="color: red;">{{ error }}</span><br>
+            {% endfor %}
+        </div>
+    </div>
+
+    <div class="mt-3">
+        {{ form.submit(class="btn btn-primary") }}
+    </div>
+</form>
+
+{% endblock %}

