+    <!-- Contenido -->
+    <div class="content">
+        <h1>User Summary</h1>
+
+        <div class="section">
+            <h2>Name:</h2>
+            <p>{{ user_data.name }}</p>
+        </div>
+        <div class="section">
+            <h2>Email:</h2>
+            <p>{{ user_data.email }}</p>
+        </div>
+        <div class="section">
+            <h2>ORCID:</h2>
+            <p>{{ user_data.orcid }}</p>
+        </div>
+        <div class="section">
+            <h2>Affiliation:</h2>
+            <p>{{ user_data.affiliation }}</p>
+        </div>
+
+        <hr>
+
+        <h2>Datasets Information</h2>
+        <div class="section">
+            <h3>Total Synchronized Datasets:</h3>
+            <p>{{ user_data.total_synchronized_datasets }}</p>
+        </div>
+        <div class="section">
+            <h3>My Unsynchronized Datasets:</h3>
+            <p>{{ user_data.my_unsynchronized_datasets }}</p>
+        </div>
+        <div class="section">
+            <h3>My Dataset Downloads:</h3>
+            <p>{{ user_data.total_dataset_downloads }}</p>
+        </div>
+        <div class="section">
+            <h3>My Dataset Views:</h3>
+            <p>{{ user_data.total_dataset_views }}</p>
+        </div>
+
+        <hr>
+
+        <h2>Feature Models Information</h2>
+        <div class="section">
+            <h3>Total Feature Models:</h3>
+            <p>{{ user_data.total_feature_models }}</p>
+        </div>
+        <div class="section">
+            <h3>My Feature Model Downloads:</h3>
+            <p>{{ user_data.total_feature_model_downloads }}</p>
+        </div>
+        <div class="section">
+            <h3>My Feature Model Views:</h3>
+            <p>{{ user_data.total_feature_model_views }}</p>
+        </div>
+
+        <hr>
+
+        <h2>Authors and Teams</h2>
+        <div class="section">
+            <h3>Total Authors:</h3>
+            <p>{{ user_data.total_authors }}</p>
+        </div>
+        <div class="section">
+            <h3>Total Teams:</h3>
+            <p>{{ user_data.total_teams }}</p>
+        </div>
+
+
+
+        <hr>
+
+        <h2>Latest Unsynchronized Datasets</h2>
+        <div class="section">
+            {% if user_data.latest_unsynchronized_datasets %}
