+
+        const queryInput = document.getElementById('query');
+        queryInput.value = queryParam
+        queryInput.dispatchEvent(new Event('input', {bubbles: true}));
+        console.log("throw event");
+
+    } else {
+        const queryInput = document.getElementById('query');
+        queryInput.dispatchEvent(new Event('input', {bubbles: true}));
+    }
+});
